mapper.select:
{ CALL BIDDER_COUNT(
					  #{pno, mode=IN, jdbcType=NUMERIC}
					, #{result, mode=OUT, jdbcType=NUMERIC, javaType=integer}
					)
}

SELECT COUNT(*)
  FROM BIDDER
 WHERE PNO = #{pno}

mapper.insert:
INSERT INTO PRODUCT
VALUES (SEQ_PNO.NEXTVAL
	  , #{pWriter}
	  , #{pcno}
	  , #{pImg}
	  , #{pTitle}
	  , #{pContent}
	  , #{pPrice}
	  , TO_CHAR(SYSTIMESTAMP, 'YYYY/mm/dd hh24:mi:ss.ff2')
	  , 1
	  , 2
	  , #{pAddress}
	  , DEFAULT
	  , NULL)
