mapper.select:
SELECT * FROM PCOMMENT 
WHERE PNO = #{pno}
ORDER BY PCDATE DESC

mapper.update:
UPDATE PCOMMENT

	PCCONTENT = #{pcContent}

WHERE PNO = #{pno} AND PCMNO = #{pcmno}

UPDATE PRODUCT
PTITLE = #{pTitle}, PPRICE = #{pPrice}, PADDRESS = #{pAddress}, PCONTENT = #{pContent}, PCNO = #{pcno}
PIMG = #{pImg}
WHERE PNO = #{pno} AND PTYPE = 1
