mapper.update:
UPDATE QNA SET
QTITLE =#{qTitle},
QCONTENT =#{qContent}
WHERE QNO =#{qno}

mapper.select:
SELECT COUNT(*)
FROM QCOMMENT
WHERE REF_QCNO =#{qcno}

SELECT QWRITER
FROM QCOMMENT
WHERE QCNO =#{qcno}

SELECT *
  FROM (
		SELECT ROW_NUMBER() OVER (PARTITION BY QNO ORDER BY CK DESC) NUM, A.*
		  FROM(
		      SELECT Q.*,(CASE WHEN QC.QWRITER LIKE 'adminbunny%' THEN 'Y'
		                  ELSE 'N' END) AS CK
		        FROM QNA Q
		        LEFT JOIN QCOMMENT QC ON Q.QNO = QC.QNO
		       ORDER BY Q.QDATE DESC, (CASE CK WHEN 'Y' THEN 1 ELSE 2 END) DESC
		)A )



	NUM =1
	
