		AND CK LIKE #{myPattern}
	
	
		 AND QTITLE LIKE #{myPattern} OR QWRITER LIKE #{myPattern} OR QCONTENT LIKE #{myPattern} OR CK LIKE #{myPattern}
	 

ORDER BY CK, QDATE

